	WHEN '{#Environment}' = 'Test' AND '{#Type_of_certificate}' = 'd' THEN 'dbo.WCC_RegistrarsDeaths_Test'
	WHEN '{#Environment}' = 'Test' AND '{#Type_of_certificate}' = 'm' THEN 'dbo.WCC_RegistrarsMarriages_Test'
	WHEN '{#Environment}' = 'Live' AND '{#Type_of_certificate}' = 'b' THEN 'dbo.WCC_RegistrarsBirths_Live'
	WHEN '{#Environment}' = 'Live' AND '{#Type_of_certificate}' = 'd' THEN 'dbo.WCC_RegistrarsDeaths_Live'
	WHEN '{#Environment}' = 'Live' AND '{#Type_of_certificate}' = 'm' THEN 'dbo.WCC_RegistrarsMarriages_Live'
	END

if '{#Match_type}' = 'exact'
set @Match_type = 'upper';
else
set @Match_type = 'soundex';

set @where_statement = @Match_type +  '(surname) = ' + @Match_type +  '(''' + '{#Cert_Surname}' + ''')'

if len('' + '{#Cert_Forename}' + '') = 1
set @where_statement +=  ' and (upper(substring(firstname,1,1)) = upper(''' + '{#Cert_Forename}' + '''))';

if len('' + '{#Cert_Forename}' + '') > 1
set @where_statement +=  ' and ' + @Match_type + '(firstname) = ' + @Match_type + '(''' + '{#Cert_Forename}' + ''')';

if ('{#Type_of_certificate}' = 'm' and '{#spouse_surname}' <> '')
set @where_statement +=  ' and ' + @Match_type + '(spouse_surname) = ' + @Match_type + '(''{#spouse_surname}'')';

