if ('{#Type_of_certificate}' = 'm' and len('{#spouse_forename}') > 1)
set @where_statement +=  ' and ' + @Match_type + '(spouse_firstname) = ' + @Match_type + '(''{#spouse_forename}'')';

if('' + '{#Cert_Area}' + '' <> 'All')
set @where_statement +=  ' and SUBSTRING(reference, 1, 3) = ''' + '{#Cert_Area}' + '''';

