set @sql = 'select 0 as search_status, COUNT(*) OVER () as total_rows, year_date as cert_year, surname as cert_surname, firstname as cert_firstname, reference as cert_ref, case SUBSTRING(reference, 1, 3) when ''767'' then ''Atherstone'' when ''768'' then ''Nuneaton and Bedworth'' when ''770'' then ''Rugby'' when ''773'' then ''Stratford'' when ''775'' then ''Warwick'' end as cert_registers_at, ''' + '{#Type_of_certificate}' + ''' as cert_type  from ' + @tablename + '  where ' + @where_statement

exec (@sql)","results_page_columns_db":"{
