	@reflist767 varchar(255) = '',
	@reflist768 varchar(255) = '',
	@count770 int = 0,
	@count773 int = 0,
	@count775 int = 0,
	@count767 int = 0,
	@count768 int = 0
	;
begin
	set @cursor = CURSOR for
