		--     set @reflist773 += @bmdtype + ':' + @bmdref + ', '
		--     set @count773 += 1
		-- end;
		if SUBSTRING(@bmdref, 1, 3) = '775' or SUBSTRING(@bmdref, 1, 3) = '773' begin -- Stratford is posted from Warwick
		    set @reflist775 += @bmdtype + ':' + @bmdref + ', '
		    set @count775 += @qty_ordered
		end;
		-- if SUBSTRING(@bmdref, 1, 3) = '767' begin
		--     set @reflist767 += @bmdtype + ':' + @bmdref + ', '
		--     set @count767 += 1
		-- end;
		if SUBSTRING(@bmdref, 1, 3) = '768' or SUBSTRING(@bmdref, 1, 3) = '767' begin -- Atherstone is posted from Nuneaton
		    set @reflist768 += @bmdtype + ':' + @bmdref + ', '
		    set @count768 += @qty_ordered
		end;
		fetch next from @cursor into @bmdref, @qty_ordered, @bmdtype
	end;

	close @cursor;
	deallocate @cursor;

	-- quick solution to empty strings upsetting substring
	if @reflist770 = ''   set @reflist770 = '.'
	if @reflist773 = ''   set @reflist773 = '.'
	if @reflist775 = ''   set @reflist775 = '.'
	if @reflist767 = ''   set @reflist767 = '.'
	if @reflist768 = ''   set @reflist768 = '.'

	select 
		SUBSTRING(@reflist770, 1, LEN(@reflist770)-1) as office770,
		SUBSTRING(@reflist773, 1, LEN(@reflist773)-1) as office773,
		SUBSTRING(@reflist775, 1, LEN(@reflist775)-1) as office775,
		SUBSTRING(@reflist767, 1, LEN(@reflist767)-1) as office767,
		SUBSTRING(@reflist768, 1, LEN(@reflist768)-1) as office768,
		@count770 as count770,
		@count773 as count773,
		@count775 as count775,
		@count767 as count767,
		@count768 as count768
	; -- len()-1 cos len ignores trailing whitespace
end;","results_page_columns_db":"","secure_fields_db_database":"","escapeTokens":"yes"}

