{"lookup_validation":"0","systemID":"55dd90215f2e0","systemName":"WCC_Dataselects","query":"DECLARE @mapIconUnselectedGold varchar(250) = '//maps.gstatic.com/mapfiles/ridefinder-images/mm_20_yellow.png';
