{"systemID":"55dd90215f2e0","systemName":"WCC_Dataselects","query":"declare @tablename varchar(50)
declare @Match_type varchar(10)
declare @where_statement varchar(2000)
declare @sql varchar(2000)

declare @rowcount varchar(2000)

set @tablename = CASE 
	WHEN '{#Environment}' = 'Test' AND '{#Type_of_certificate}' = 'b' THEN 'dbo.WCC_RegistrarsBirths_Test'
	WHEN '{#Environment}' = 'Test' AND '{#Type_of_certificate}' = 'd' THEN 'dbo.WCC_RegistrarsDeaths_Test'
	WHEN '{#Environment}' = 'Test' AND '{#Type_of_certificate}' = 'm' THEN 'dbo.WCC_RegistrarsMarriages_Test'
	WHEN '{#Environment}' = 'Live' AND '{#Type_of_certificate}' = 'b' THEN 'dbo.WCC_RegistrarsBirths_Live'
	WHEN '{#Environment}' = 'Live' AND '{#Type_of_certificate}' = 'd' THEN 'dbo.WCC_RegistrarsDeaths_Live'
	WHEN '{#Environment}' = 'Live' AND '{#Type_of_certificate}' = 'm' THEN 'dbo.WCC_RegistrarsMarriages_Live'
	END

if '{#Match_type}' = 'exact'
set @Match_type = 'upper';
else
set @Match_type = 'soundex';

set @where_statement = @Match_type +  '(surname) = ' + @Match_type +  '(''' + '{#Cert_Surname}' + ''')'

if len('' + '{#Cert_Forename}' + '') = 1
set @where_statement +=  ' and (upper(substring(firstname,1,1)) = upper(''' + '{#Cert_Forename}' + '''))';

if len('' + '{#Cert_Forename}' + '') > 1
set @where_statement +=  ' and ' + @Match_type + '(firstname) = ' + @Match_type + '(''' + '{#Cert_Forename}' + ''')';

if ('{#Type_of_certificate}' = 'm' and '{#spouse_surname}' <> '')
set @where_statement +=  ' and ' + @Match_type + '(spouse_surname) = ' + @Match_type + '(''{#spouse_surname}'')';

if ('{#Type_of_certificate}' = 'm' and len('{#spouse_forename}') = 1)
set @where_statement +=  ' and (substring(spouse_firstname,1,1) = ''{#spouse_forename}'')';

if ('{#Type_of_certificate}' = 'm' and len('{#spouse_forename}') > 1)
set @where_statement +=  ' and ' + @Match_type + '(spouse_firstname) = ' + @Match_type + '(''{#spouse_forename}'')';

if('' + '{#Cert_Area}' + '' <> 'All')
set @where_statement +=  ' and SUBSTRING(reference, 1, 3) = ''' + '{#Cert_Area}' + '''';

set @where_statement +=  ' and year_date between ''' + convert(varchar(4),'{#Start_year}') + ''' and ''' + convert(varchar(4),'{#End_year}') +''''

set @sql = 'select 0 as search_status, COUNT(*) OVER () as total_rows, year_date as cert_year, surname as cert_surname, firstname as cert_firstname, reference as cert_ref, case SUBSTRING(reference, 1, 3) when ''767'' then ''Atherstone'' when ''768'' then ''Nuneaton and Bedworth'' when ''770'' then ''Rugby'' when ''773'' then ''Stratford'' when ''775'' then ''Warwick'' end as cert_registers_at, ''' + '{#Type_of_certificate}' + ''' as cert_type  from ' + @tablename + '  where ' + @where_statement

exec (@sql)","results_page_columns_db":"{
	"cert_year":"Year",
	"cert_surname":"Surname",
	"cert_firstname":"Firstname",
	"cert_ref":"Reference",
	"cert_registers_at":"Registers held at"
}","secure_fields_db_database":"","escapeTokens":"yes"}

